const { stringToNumber } = require('.');
const { strictEqual, throws } = require('assert');

strictEqual(stringToNumber('0'), 0);
strictEqual(stringToNumber('1'), 1);
strictEqual(stringToNumber('1.23'), 1.23);
strictEqual(stringToNumber('123'), 123);
strictEqual(stringToNumber('12.345'), 12.345);
strictEqual(stringToNumber('-0'), 0);
strictEqual(stringToNumber('-1'), -1);
strictEqual(stringToNumber('-1.23'), -1.23);
strictEqual(stringToNumber('-123'), -123);
strictEqual(stringToNumber('-12.345'), -12.345);

strictEqual(stringToNumber(Number.MAX_VALUE.toString()), Number.MAX_VALUE);
strictEqual(stringToNumber(Number.MIN_VALUE.toString()), Number.MIN_VALUE);
strictEqual(stringToNumber(Number.MAX_SAFE_INTEGER.toString()), Number.MAX_SAFE_INTEGER);
strictEqual(stringToNumber(Number.MIN_SAFE_INTEGER.toString()), Number.MIN_SAFE_INTEGER);
strictEqual(stringToNumber('5e-324'), 5e-324);
strictEqual(stringToNumber('-5e-323'), -5e-323);
strictEqual(stringToNumber('1.7976931348623157e+308'), 1.7976931348623157e+308);
strictEqual(stringToNumber('-1.7976931348623157e+306'), -1.7976931348623157e+306);

strictEqual(stringToNumber('-1e+0'), -1);
strictEqual(stringToNumber('-1e-0'), -1);

strictEqual(stringToNumber('123.456789876543'), 123.456789876543);
strictEqual(stringToNumber('-123.456789876543'), -123.456789876543);
strictEqual(stringToNumber('1.110223024625155e-16'), 1.110223024625155e-16);
strictEqual(stringToNumber('-1.110223024625155e-16'), -1.110223024625155e-16);
strictEqual(stringToNumber('9007199254740991'), 9007199254740991);
strictEqual(stringToNumber('-9007199254740991'), -9007199254740991);
strictEqual(stringToNumber('5e-324'), 5e-324);
strictEqual(stringToNumber('1.7976931348623157e+308'), 1.7976931348623157e+308);
strictEqual(stringToNumber('0.00999'), 0.00999);
strictEqual(stringToNumber('123.456789'), 123.456789);
strictEqual(stringToNumber('1.23456789876543'), 1.23456789876543);
strictEqual(stringToNumber('123.456789876543'), 123.456789876543);
strictEqual(stringToNumber('-123.456789876543'), -123.456789876543);
strictEqual(stringToNumber('1.1102230246251564e-16'), 1.1102230246251564e-16);
strictEqual(stringToNumber('-1.1102230246251564e-16'), -1.1102230246251564e-16);
strictEqual(stringToNumber('9007199254740991'), 9007199254740991);
strictEqual(stringToNumber('-9007199254740991'), -9007199254740991);
strictEqual(stringToNumber('5e-324'), 5e-324);
strictEqual(stringToNumber('1.7976931348623157e+308'), 1.7976931348623157e+308);
strictEqual(stringToNumber('0.00999'), 0.00999);
strictEqual(stringToNumber('123.456789'), 123.456789);
strictEqual(stringToNumber('1.23456789876543'), 1.23456789876543);

throws(() => stringToNumber('5e-325'));
throws(() => stringToNumber('-5e-325'));
throws(() => stringToNumber('1.7976931348623157e+309'));
throws(() => stringToNumber('-1.7976931348623157e+309'));
throws(() => stringToNumber('123.'));
throws(() => stringToNumber('-123.'));
throws(() => stringToNumber('.7'));
throws(() => stringToNumber('-.7'));
